package com.example.chess;

import javafx.application.Application;
import javafx.scene.Group;
import javafx.scene.Scene;
import javafx.scene.image.ImageView;
import javafx.scene.paint.Color;
import javafx.scene.shape.Rectangle;
import javafx.stage.Stage;

import java.io.IOException;
import java.util.ArrayList;

public class ChessBoard extends Application {
    int Rows = 8;
    int Columns = 8;
    int SquareSize = 75;
    String Pawn = "data:image/png;base64,";
    String Knight = "data:image/png;base64,";
    String Bishop = "data:image/png;base64,";
    String Rook = "data:image/png;base64,";
    String Queen = "data:image/png;base64,";
    String King = "data:image/png;base64,";
    Rectangle[][] BoardBackground = new Rectangle[Rows][Columns];
    ImageView[][] Pieces = new ImageView[Rows][Columns];
    @Override
    public void start(Stage stage) throws IOException {
        Group group = new Group();
        for (int i = Columns-1; i >= 0; i--) {
            for (int j = 0; j < Rows; j++) {
                BoardBackground[i][j] = new Rectangle(SquareSize*i,SquareSize*j,SquareSize,SquareSize);
                if((i+j)%2==0) {
                    BoardBackground[i][j].setFill(Color.LIGHTGREY);
                }
                else {
                    BoardBackground[i][j].setFill(Color.BLACK);

                }
                group.getChildren().add(BoardBackground[i][j]);
            }
        }
        String startfen = "rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR ";
        int row = 0;
        int column = 8;
        while(!(startfen.charAt(0) == ' ')){
            if (row == 8){
                row = 0;
                column--;
            }
            if(startfen.charAt(0) == 'r' || startfen.charAt(0) == 'R'){
                startfen = startfen.substring(1);


            }
            row++;
        }
        Scene scene = new Scene(group);
        stage.setScene(scene);
        stage.show();

    }

    public static void main(String[] args) {
        launch();
    }
}